import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { timer } from 'rxjs';
import { list, onShowDetail } from './animations';
let AppComponent = class AppComponent {
    constructor(currenciesService, routerService) {
        this.currenciesService = currenciesService;
        this.routerService = routerService;
        this.currencies$ = this.currenciesService.getCurrencies();
    }
    showDetail(currency) {
        this.showLoader = currency.code;
        this.selected = currency.code;
        timer(2000).subscribe(_ => (this.showLoader = "",
            this.routerService.navigate([
                "currency-detail", `${currency.code} ${currency.country}`
            ])));
    }
    onShowDetail() {
        return this.routerService.url.split("/")[1];
    }
};
AppComponent = __decorate([
    Component({
        selector: "app-root",
        template: `
    <app-navbar>
      <app-navbar-logo></app-navbar-logo>
      <app-navbar-title></app-navbar-title>
    </app-navbar>
    <app-search-currency #search></app-search-currency>

    <app-currency-list-and-detail *ngIf="currencies$ | async as currencies">
      <app-currency-list [@onShowDetail]="onShowDetail()" @list>
        <ng-container *ngFor="let currency of currencies | sortbycountry">

              <app-currency
                (click)="showDetail(currency)"
                [currency]="currency"
                [selected]="selected"
                [filteredCurrency]="(search.searchedCurrency.value | filter: currency)"
              >
                <app-loader *ngIf="showLoader === currency.code"></app-loader>
              </app-currency>

          </ng-container>
        </ng-container>
      </app-currency-list>

      <app-currency-detail>
        <router-outlet></router-outlet>
      </app-currency-detail>
    </app-currency-list-and-detail>
  `,
        styles: [
            `
      :host {
        display: flex;
        flex-direction: column;
        align-items: center;
        box-sizing: border-box;
        width: 100vw;
        height: 100vh;
        color: white;
        background-image: linear-gradient(
          rgba(68, 67, 109, 1),
          rgba(49, 48, 94, 1)
        );
      }
    `,
        ],
        animations: [list, onShowDetail],
    })
], AppComponent);
export { AppComponent };
