let DetailComponent = class DetailComponent {
    constructor(router, currencyService, activeRoute) {
        this.router = router;
        this.currencyService = currencyService;
        this.activeRoute = activeRoute;
    }
    // closeDetail(): void {
    //   this.router.navigate(["/"]);
    //   this.currencyService.selected$.next('');
    // }
    ngOnDestroy() {
        this.currencyService.selected$.next('');
    }
    ngOnInit() {
        this.currencyDetail$ = this.activeRoute.params.pipe(switchMap((params) => this.currencyService.getCurrencyDetail(params["currency"])));
    }
};
